// SPDX-License-Identifier: MIT
pragma solidity >=0.5.16;
import "./DocuLibrary.sol";

contract DocuNet {
    // File Contract
    event FileCreated(
        uint Filecount,
        string hash,
        string description,
        address owner
        );

    //store files
    //uint fileCount = 0;
    //mapping(address => File) public files;
    mapping(address => uint) public fileCount;
    mapping(address => mapping(uint => DocuLibrary.File)) files;

    //create files
    function uploadFile(string memory _fileHash, string memory _description) public{

        // make sure the file hash exists
        require(bytes(_fileHash).length > 0);

        //make sure file description exists
        require(bytes(_description).length > 0);

        //make sure uploader address exists
        require(msg.sender != address(0x0));

        //increment Filecounter
        //fileCount ++;

        //Add files to contract
        files[msg.sender][fileCount[msg.sender]++] = DocuLibrary.File( _fileHash, _description, msg.sender);

        //Trigger an event that file has been uploaded
        emit FileCreated(fileCount[msg.sender], _fileHash, _description, msg.sender);
    }

    function getFile(address _owneraddress, uint _filenumber) external view returns(string memory){
        string storage getFileHash = files[_owneraddress][_filenumber].hash;
        return getFileHash;
    }

    // File Transfer
    address public owner = msg.sender;
    //Structure
    mapping (address=>string) public ipfsInbox;

    //Events
    event ipfsSent(string _ipfsHash, address payable _address);
    event inboxResponse(string response);

    //Modifiers
    modifier notFull (string memory _string) {
    bytes memory stringTest = bytes(_string); 
    require(stringTest.length==0); 
    _;
    }
    modifier restricted() {
    require(
      msg.sender == owner,
      "This function is restricted to the contract's owner"
    );
    _;
    }

    //takes in receiver's address and IPFS address. Places the IPFSadress in the receiver's inbox
    function sendIPFS(address payable _address, string memory _ipfsHash) notFull(ipfsInbox[_address]) public{
        ipfsInbox[_address] = _ipfsHash;
        emit ipfsSent(_ipfsHash, _address);
    }

    //check your inbox and empties it afterwards
    function checkInbox() public restricted{
        string memory ipfs_hash=ipfsInbox[msg.sender];
        if(bytes(ipfs_hash).length==0){
            emit inboxResponse("Empty Inbox");
        }else{
            ipfsInbox[msg.sender]="";
            emit inboxResponse(ipfs_hash);
        }
    }

    //retrieves hash
    function getHash(address _address) public{
        string memory ipfs_hash=ipfsInbox[_address];
        emit inboxResponse(ipfs_hash);
    }

    // School Contract
    address payable[] public schoolStudents;
    bool studentInSchool;
    address[] public schoolStudent;

    address[] public Records;
    address[] public addressValues;

    mapping (address => address) public mappedStudents;

    function addStudent( address _schoolAddress, address _studentAddress, string memory _studentName, string memory _firstname, string memory _lastname, string memory _email) public{
        DocuLibrary.User storage user = users[_studentAddress];
        require(!user.set);
        users[_studentAddress] = DocuLibrary.User({
        // id: _userId,
        school: _schoolAddress,
        name: _studentName,
        firstname: _firstname,
        lastname: _lastname,
        email: _email,
        set: true
        });

        Records.push(_studentAddress);
        mappedStudents[_studentAddress] = _schoolAddress;
    }

    function hasStudent(address _stuAddress) public view returns(bool){
        if(users[_stuAddress].set == true){
            return true;
        }
        else return false;
    }

    function getStudent(address _stuAddress) public  view returns(string memory){
        string memory answer = users[_stuAddress].name;
        return answer;
    }

    function getStudents(address _schoolAddress) public returns(address[] memory){
        delete addressValues;
        for(uint i=0; i< Records.length; i++) {
            if (mappedStudents[Records[i]]==_schoolAddress) {
                addressValues.push(Records[i]);
            }
        }

        return addressValues;
    }

    function studentCount(address _schoolAddress) public returns(uint){
        delete addressValues;
        for(uint i=0; i< Records.length; i++) {
            if (mappedStudents[Records[i]]==_schoolAddress) {
                addressValues.push(Records[i]);
            }
        }

        return addressValues.length;
    }
    //other functions remaining.
    // User Contract
    mapping(address => DocuLibrary.User) public users;
    function createUser(address _schoolAddress, address _userAddress, string memory _userName, string memory _firstname, string memory _lastname, string memory _email) public {
    DocuLibrary.User storage user = users[_userAddress];
    // Check that the user did not already exist:
    require(!user.set);
    //Store the user
    users[_userAddress] = DocuLibrary.User({
        school: _schoolAddress,
        // id: _userId,
        name: _userName,
        firstname: _firstname,
        lastname: _lastname,
        email: _email,
        set: true
    });
    }

    //mapping(uint256 => bytes32) public userDataHashes; // to store user userDataHashes

    // function storeUserDataHash(uint256 _userId, bytes32 _dataHash) public {
    // userDataHashes[_userId] = _dataHash;
    // }

    function getuserDataName(address _userAddress) external view returns (string memory) {
        string storage getUserName = users[_userAddress].name;
        return getUserName;
    }
}
